import * as Utils from '../assets/function/utils.js';
export default {
  name: 'cmp-select-category',
  props: {
    subcategories: [],
    models: [{
      catgoryId: String,
      subcategoryId: String
    }],
    addAble: Boolean
  },
  data() {
    return {
      subcategoryId: "",
      subcategoryOptions: []
    };
  },
  methods: {
    /* 中カテゴリのIDに対応する名称を返す */
    getSubcategoryName(subcategoryId) {
      //return Utils.getMasterName(this.subcategories, subcategoryId);
      return Utils.getMasterName(this.addOptions(this.createSubCategories), subcategoryId);
    },
    /* 選択した大カテゴリから、中カテゴリを絞り込んで返却  */
    createSubCategories() {
      return Utils.createSubCategories(this.subcategories, this.models.categoryId);
    },
    /* 「新規追加」「未選択」をリストに追加 */
    addOptions(list) {
      const newOption = list.filter(item => {
        return item.id == "new";
      });
      const notSelectedOption = list.filter(item => {
        return item.id == "";
      });
      if (this.addAble) {
        if (newOption.length == 0) {
          list.unshift({
            id: "new",
            name: "新規追加"
          });
        }
      }
      if (notSelectedOption.length == 0) {
        list.unshift({
          id: "",
          name: "未選択"
        });
      }
      return list;
    }
  },
  created() {
    this.subcategoryId = this.models.subcategoryId;
  },
  watch: {
    subcategoryId(newValue) {
      this.$emit('update:subcategoryId', newValue);
    }
  }
};
